import java.util.Arrays;
public class Sorting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 
		    
		    int[] array1 = {45,75,12,33,95,423,543,8634,232,0,1,7};
		            
		    String[] array2 = {"Ana","Pattinson","michale","tom","Luke","swift","taylor","Andrew"};
		    
		    System.out.println("\nOriginal array : "+Arrays.toString(array1));
		    Arrays.sort(array1);
		    
		    System.out.println("\nSorted array : "+Arrays.toString(array1));
		    
		    System.out.println("\nOriginal array : "+Arrays.toString(array2));
		    
		    Arrays.sort(array2);
		    
		    System.out.println("\nSorted array : "+Arrays.toString(array2));
		    }
		


	}


